package job

import (
	"sync"

	dlog "github.com/dyweb/gommon/log"
)

// Executor on central node dispatch plan generated by planner on multiple nodes one stage at a time
// TODO: dispatcher or executor
// interface for executor so we can have executor that
// - simply print command out (for dry run)
// - run everything locally, assume the job spec can be run locally, i.e. everything is localhost
// - dispatch job to remote agent
type Executor struct {
	mu sync.RWMutex

	log *dlog.Logger
}

func NewExecutor() *Executor {
	exc := &Executor{}
	dlog.NewStructLogger(log, exc)
	return exc
}
